const now = new Date();
const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
const tomorrow = new Date(today);
tomorrow.setDate(today.getDate() + 1);

const isoDate = (d) => d.toISOString().split('.')[0] + 'Z';

return [
  {
    json: {
      today: isoDate(today),
      tomorrow: isoDate(tomorrow),
    },
  },
];

const cleanMessages = items.map(item => {
  const from = item.json.header?.from || "Unknown sender";
  const subject = item.json.header?.subject || "No subject";
  const rawBody = item.json.text || ""; // fall back to empty string
  const body = String(rawBody); // ensure it's a string

  const cleaned = body
    .replace(/<\/?[^>]+(>|$)/g, "")             // Remove HTML
    .replace(/(Unsubscribe|View in browser)/gi, "") // Strip only promo triggers
    .replace(/\n{3,}/g, "\n\n")                 // Collapse excess breaks
    .trim();                                    // Clean up

  return `📩 **From:** ${from}\n**Subject:** ${subject}\n**Preview:**\n${cleaned}\n\n---\n`;
});

return [{
  json: {
    combinedText: cleanMessages.join('\n')
  }
}];

const markdown = $input.all().map((item) => item.json.output);
let html = markdown.map((md) =>
  md
    .replace(/(\*\*|__)(.*?)\1/g, "<b>$2</b>")
    .replace(/(\*|_)(.*?)\1/g, "<i>$2</i>")
    .replace(/~~(.*?)~~/g, "<del>$1</del>")
    .replace(/`([^`\n]+)`/g, "<code>$1</code>")
    .replace(/!\[([^\]]+)\]\(([^\)]+)\)/g, '<img src="$2" alt="$1">')
    .replace(/\[([^\]]+)\]\(([^\)]+)\)/g, '<a href="$2">$1</a>')
    .replace(/(\r\n|\n|\r)/gm, "<br>"),
);

return { html };
